import React, { Component } from "react";
import Header from "./components/Header";
import EmployeeTable from "./components/EmployeeTable";
import Navbar from "./components/Navbar";
import "bootstrap/dist/css/bootstrap.css";
import API from "./Utils/API";

class App extends Component {
  state = {
    results: [],
    filtered: [],
  };

  componentDidMount() {
    API.search().then((res) =>
      this.setState({ results: res.data.results, filtered: res.data.results })
    );
  }
  sortByName = () => {
    this.setState({
      results: [...this.state.results].sort((a, b) => {
        return a.name.first.localeCompare(b.name.first);
      }),
    });
  };
  handleSearchChange = () => {
    const [filteredEmployees, setFilteredEmployees] = useState([]);
    //get input value - change to lowercase
    const inputVal = document.querySelector("#search").value.toLowerCase();
    //array filter
    const filteredEmployeeArr = users.filter(
      (employee) => employee.nameF.toLowerCase().indexOf(inputVal) > -1
    );
    //set the filtered data to state
    setFilteredEmployees(filteredEmployeeArr);
  };

  render() {
    return (
      <div className="App">
        <Header />
        <input id="search" onChange={handleSearchChange} />
        <EmployeeTable
          employeeList={this.state.filter}
          sortByName={this.sortByName}
        />
      </div>
    );
  }
}

export default App;
