import React, { Component } from "react";

class Navbar extends Component {
  state = {
    results: [],
  };
  filterByName = () => {
    const userSearch = document.getElementById("search").value.toLowerCase();
    const filteredEmployees = this.state.results.filter(
      (employee) => employee.name.first.toLowerCase().indexOf(userSearch) > -1
    );
    this.setState({
      filtered: filteredEmployees,
    });
  };
  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col"></div>
          <div className="col">
            <nav>
              <button
                class="btn btn-outline-success my-2 my-sm-0"
                type="submit"
                onClick= {() => {
                  this.props.filterByName(document.getElementById("search"))
                }}
              >
                Search
              </button>{" "}
              <input id="search" placeholder="name" type="search" />
            </nav>
          </div>
          <div className="col"></div>
        </div>
      </div>
    );
  }
}
export default Navbar;
